INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149293, 'Sibutramine', 'Oxymetazoline (nasal)', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when sibutramine is combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. Sibutramine is a potent inhibitor of norepinephrine and serotonin reuptake, and a less potent inhibitor of dopamine reuptake.', NULL, 'Caution is advised if sibutramine is coadministered with other drugs that can increase blood pressure and/or heart rate.', NULL, 'Caution is advised if sibutramine is coadministered with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating therapy with sibutramine and monitored at regular intervals thereafter. Dose reduction or discontinuation of sibutramine should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Dexfenfluramine, Naltrexone, Ephedrine, Fenfluramine, Phentermine, Orlistat, Lorcaserin, Diethylpropion, Mazindol', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149456/', '[1] "Product Information. Meridia (sibutramine)." Knoll Pharmaceutical Company, Whippany, NJ.[2] Mendelson J, Jones RT, Upton R, Jacob P 3rd "Methamphetamine and ethanol interactions in humans." Clin Pharmacol Ther 57 (1995): 559-68[3] "Product Information. Didrex (benzphetamine)" Pharmacia and Upjohn, Kalamazoo, MI.[4] "Product Information. Suprenza (phentermine)." Akrimax Pharmaceuticals, Cranford, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149294, 'Solriamfetol', 'Oxymetazoline (nasal)', 'Moderate', 'Additive or synergistic effects on blood pressure and/or heart rate may occur when solriamfetol is combined with other drugs that increase blood pressure and/or heart rate. The use of solriamfetol alone has been associated with dose-dependent increases in blood pressure and heart rate.', NULL, 'Caution is advised if solriamfetol is used with other drugs that increase blood pressure and/or heart rate.', NULL, 'Caution is advised if solriamfetol is used with other drugs that increase blood pressure and/or heart rate. Blood pressure should be monitored prior to initiating solriamfetol therapy and regularly during treatment. In addition, hypertension should be controlled prior to treatment, and any new-onset hypertension or exacerbations of pre-existing hypertension that occur during solriamfetol therapy should be treated. Discontinuation of solriamfetol should be considered in patients who experience an increase in blood pressure or heart rate that cannot be managed with dose reduction of solriamfetol or other appropriate medical intervention.', 'Synergism', 'Amphetamine, Pemoline, Atomoxetine, Metamfetamine, Lisdexamfetamine, Caffeine, Methylphenidate, Dexmethylphenidate, Dextroamphetamine, Armodafinil, Citicoline, Modafinil', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149457/', '[1] "Product Information. Sunosi (solriamfetol)." Jazz Pharmaceuticals, Palo Alto, CA.', 1767369485);
