INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149551, 'Phenylpropanolamine', 'Oxytocin', 'Moderate', 'Concomitant use of oxytocic agents with sympathomimetic amines may increase the risk of hypertension due to additive vasoconstrictive effects. Severe hypertension and headache have been reported when oxytocin was given 3 to 4 hours following prophylactic administration of a vasoconstrictor in conjunction with caudal block anesthesia.', NULL, 'Close monitoring of blood pressure is recommended during coadministration.', NULL, 'Close monitoring of blood pressure is recommended during coadministration.', 'Synergism', 'Misoprostol, Dinoprostone, Terlipressin, Desmopressin', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149714/', '[1] "Product Information. Syntocinon (oxytocin)." Sandoz Pharmaceuticals Corporation, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Mendelson J, Jones RT, Upton R, Jacob P 3rd "Methamphetamine and ethanol interactions in humans." Clin Pharmacol Ther 57 (1995): 559-68[4] "Product Information. Didrex (benzphetamine)" Pharmacia and Upjohn, Kalamazoo, MI.[5] "Product Information. Suprenza (phentermine)." Akrimax Pharmaceuticals, Cranford, NJ.', 1767369485);
