INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149567, 'Ranolazine', 'Oxytocin', 'Moderate', 'Ranolazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if ranolazine is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if ranolazine is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of ranolazine, the maximum recommended dosage of 1000 mg twice daily should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Ranolazine is contraindicated in patients with liver cirrhosis because of the profound effect on QT prolongation in this population.', 'Synergism', 'Misoprostol, Dinoprostone, Terlipressin, Desmopressin', 'Alprostadil, Indomethacin, Ibuprofen, Ubidecarenone, Regadenoson', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149730/', '[1] European Medicines Agency "CHMP Assessment Report for Latixa. International nonproprietary name: ranolazine. Procedure No. EMEA/H/C/805. Available from: URL: http://www.emea.europa.eu/humandocs/PDFs/EPAR/latixa/H-805-en6.pdf." ([cited 2008]):[2] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[3] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[6] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[7] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149568, 'Relugolix', 'Oxytocin', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'Methylergometrine, Misoprostol, Ergometrine, Dinoprostone, Terlipressin, Desmopressin', 'Norethisterone, Nafarelin, Octreotide, Elagolix, Cetrorelix, Estradiol, Ganirelix, Lanreotide, Gonadorelin, Anastrozole, Fulvestrant, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149731/', '[1] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc), Lexington, MA.[2] "Product Information. Zoladex (goserelin)." Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Trelstar (triptorelin)" Pharmacia and Upjohn, Kalamazoo, MI.[5] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[6] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc, Deerfield, IL.[7] "Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals, New York, NY.[8] "Product Information. Plenaxis (abarelix)." Praecis Pharmaceuticals Inc, Waltham, MA.', 1767369485);
