INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149622, 'Bifidobacterium longum infantis', 'Ozanimod', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy. It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed.', 'Others', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149785/', '[1] Borriello SP, Hammes WP, Holzapfel W, et.al "Safety of probiotics that contain lactobacilli or bifidobacteria." Clin Infect Dis 36 (2003): 775-80[2] Salminen MK, Tynkkynen S, Rautelin H, et al. "Lactobacillus bacteremia during a rapid increase in probiotic use of Lactobacillus rhamnosus GG in Finland." Clin Infect Dis 35 (2002): 1155-60[3] Schlegel L, Lemerle S, Geslin P "Lactobacillus species as opportunistic pathogens in immunocompromised patients." Eur J Clin Microbiol Infect Dis 17 (1998): 887-8[4] Salminen MK, Rautelin H, Tynkkynen S, et al. "Lactobacillus bacteremia, clinical significance, and patient outcome, with special focus on probiotic L. rhamnosus GG." Clin Infect Dis 38 (2004): 62-9[5] Husni RN, Gordon SM, Washington JA, Longworth DL "Lactobacillus bacteremia and endocarditis: review of 45 cases." Clin Infect Dis 25 (1997): 1048-55[6] Boyle RJ, Robins-Browne RM, Tang ML "Probiotic use in clinical practice: what are the risks?" Am J Clin Nutr 83 (2006): 1256-64[7] Lolis N, Veldekis D, Moraitou H, et.al "Saccharomyces boulardii fungaemia in an intensive care unit patient treated with caspofungin." Crit Care 12 (2008): epub[8] Rautio M, Jousimies-Somer H, Kauma H, et al. "Liver abscess due to a Lactobacillus rhamnosus strain indistinguishable from L. rhamnosus strain GG." Clin Infect Dis 28 (1999): 1159-60[9] Saxelin M, Chuang NH, Chassy B, et.al "Lactobacilli and bacteremia in southern Finland, 1989-1992" Clin Infect Dis 22 (1996): 564-6[10] Oggioni MR, Pozzi G, Valensin PE, Galieni P, Bigazzi C "Recurrent septicemia in an immunocompromised patient due to probiotic strains of Bacillus subtilis." J Clin Microbiol 36 (1998): 325-6[11] Mackay AD, Taylor MB, Kibbler CC, Hamilton-Miller JM "Lactobacillus endocarditis caused by a probiotic organism." Clin Microbiol Infect 5 (1999): 290-2', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149623, 'Brexucabtagene autoleucel', 'Ozanimod', 'Major', 'Coadministration of ozanimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may increase the risk of unintended additive immunosuppressive effects. Ozanimod causes reversible sequestration of lymphocytes in lymphoid tissues. The safety and efficacy of ozanimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated.', NULL, 'Concomitant use of ozanimod with antineoplastic, immunosuppressive, or immune-modulating agents should be avoided according to some authorities (UK); however, other authorities (AU, US) advise caution and close monitoring during coadministration and for up to 3 months after the last dose of ozanimod.', NULL, 'Concomitant use of ozanimod with antineoplastic, immunosuppressive, or immune-modulating agents should be avoided according to some authorities (UK); however, other authorities (AU, US) advise caution and close monitoring during coadministration and for up to 3 months after the last dose of ozanimod. When switching from drugs with prolonged immune effects to ozanimod, the half-life and mode of action of these drugs must be considered to avoid unintended additive immunosuppressive effects while at the same time minimizing risk of disease reactivation.', 'Synergism', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, Apremilast, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149786/', '[1] "Product Information. Zeposia (ozanimod).