    ) VALUES (1368403, 'Metronidazole', 'Mebendazole', 'Moderate', 'Limited clinical data have suggested that coadministration of metronidazole and mebendazole may be associated with Stevens-Johnson syndrome/toxic epidermal necrolysis. The mechanism is unknown.', NULL, 'It is recommended that concomitant use of mebendazole and metronidazole should be avoided.', NULL, 'It is recommended that concomitant use of mebendazole and metronidazole should be avoided.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
