    ) VALUES (1368412, 'Bismuth subcitrate potassium', 'Minocycline', 'Moderate', 'INTERVAL: Administration of a bismuth-containing preparation within two to three hours of a tetracycline may significantly decrease serum tetracycline concentrations. Data are available for tetracycline and doxycycline. The proposed mechanism is chelation of tetracycline by bismuth.', NULL, 'Administration of a tetracycline and bismuth-containing preparation should be separated by two to three hours.', NULL, 'Administration of a tetracycline and bismuth-containing preparation should be separated by two to three hours. Patients should be monitored for diminished tetracycline efficacy.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
