INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (74655, 'Corticotropin', 'Losartan', 'Moderate', 'Corticosteroids may antagonize the effects of antihypertensive medications by inducing sodium and fluid retention. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) because they have greater mineralocorticoid activity. Conversely, some calcium channel blockers such as diltiazem and verapamil may increase corticosteroid plasma levels and effects by inhibiting their clearance via CYP450 3A4 metabolism.', NULL, 'Patients on prolonged (i.', NULL, 'Patients on prolonged (i.e., longer than about a week) or high-dose corticosteroid therapy should have blood pressure, electrolyte levels, and body weight monitored regularly, and be observed for the development of edema and congestive heart failure. The dosages of antihypertensive medications may require adjustment.', 'Metabolism, Antagonism', 'Sacubitril', 'Mecasermin, Sermorelin, Tesamorelin, Thyrotropin alfa, Mecasermin rinfabate, Pegvisomant', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74742/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (74656, 'Corticotropin', 'Lyme disease vaccine (recombinant OspA)', 'Moderate', 'The administration of Lyme disease vaccine to immunosuppressed patients may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'The manufacturer suggests considering deferral of vaccination against Lyme disease until 3 months after discontinuation of immunosuppressive therapy.', NULL, 'The manufacturer suggests considering deferral of vaccination against Lyme disease until 3 months after discontinuation of immunosuppressive therapy. Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'Mecasermin, Sermorelin, Tesamorelin, Thyrotropin alfa, Somatrem, Somatotropin, Mecasermin rinfabate, Pegvisomant, Somapacitan', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74743/', '[1] "Product Information. Lymerix (Lyme disease vaccine)." SmithKline Beecham, Philadelphia, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (74657, 'Corticotropin', 'Macimorelin', 'Moderate', 'Drugs that directly affect the pituitary secretion of growth hormone (e.g., somatostatin, insulin, glucocorticoids, cyclooxygenase inhibitors such as aspirin and nonsteroidal anti-inflammatory drugs) and drugs that may transiently elevate growth hormone levels (e.g., clonidine, levodopa, insulin) may impact the accuracy of the macimorelin diagnostic test.', NULL, 'Concomitant use of macimorelin with drugs affecting growth hormone levels should generally be avoided.', NULL, 'Concomitant use of macimorelin with drugs affecting growth hormone levels should generally be avoided. A sufficient washout period following discontinuation of these drugs is recommended prior to macimorelin administration.', 'Others', 'Pegvisomant, Thyrotropin alfa', 'Methacholine, Hexaminolevulinate, Secretin human, Methylene blue, Sermorelin, Sorbitol, Galactose, Vitamin A, Caffeine, Metyrapone, Histamine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74744/', '[1] "Product Information. Macrilen (macimorelin)." Aeterna Zentaris, Charleston, SC.[2] "Product Information. Macrilen (macimorelin)." Aeterna Zentaris, Charleston, SC.', 1767369485);
