INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (74737, 'Corticotropin', 'Theophylline', 'Moderate', 'The concomitant use of theophylline and corticosteroids may theoretically increase the risk of hypokalemia due to additive potassium-lowering effects. Additionally, theophylline serum concentrations may be altered.', NULL, 'Monitoring for altered efficacy and safety of theophylline and altered serum potassium and theophylline concentrations is advisable when these drugs are coadministered.', NULL, 'Monitoring for altered efficacy and safety of theophylline and altered serum potassium and theophylline concentrations is advisable when these drugs are coadministered. Patients should be advised to notify their physician if they experience signs of hypokalemia (e.g., weakness, lethargy, and muscle pains or cramps), worsening respiratory symptoms, or signs of theophylline toxicity (e.g., nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat).', 'Synergism', 'Omalizumab, Tezepelumab, Mercaptopurine, Aminophylline, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Oxtriphylline, Mepolizumab, More', 'Mecasermin, Sermorelin, Tesamorelin, Thyrotropin alfa, Mecasermin rinfabate, Pegvisomant', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74824/', '[1] Fergusson RJ, Scott CM, Rafferty P, Gaddie J "Effect of prednisolone on theophylline pharmacokinetics in patients with chronic airflow obstruction." Thorax 42 (1987): 195-8[2] Buchanan N, Hurwitz S, Butler P "Asthma - a possible interaction between hydrocortisone and theophylline." S Afr Med J 56 (1979): 1147-8[3] Leavengood DC, Bunker-Soler AL, Nelson HS "The effect of corticosteroids on theophylline metabolism." Ann Allergy 50 (1983): 249-51[4] Elvey SM, Saccar CL, Rocci ML, Mansmann HC, Martynec DM, Kester MB "The effect of corticosteroids on theophylline metabolim in asthmatic children." Ann Allergy 56 (1986): 520[5] Tatsis G, Orphanidou D, Douratsos D, et al "The effect of steroids on theophylline absorption." J Int Med Res 19 (1991): 326-9[6] Anderson JL, Ayres JW, Hall CA "Potential pharmacokinetic interaction between theophylline and prednisone." Clin Pharm 3 (1984): 187-8', 1767369485);
