INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75067, 'Tetracosactide', 'Licorice', 'Moderate', 'Licorice may potentiate the effects of corticosteroids. Licorice use has been associated with hypertension as well as sodium and water retention. Glycyrrhizic acid, a component of licorice, is hydrolyzed in the intestine to a metabolite (glycyrrhetinic acid) that causes mineralocorticoid and renin-suppressing effects. Prolonged use of licorice has led to a hypermineralocorticoid (pseudohyperaldosteronism) syndrome characterized by hypertension, hypernatremia, hypokalemia, metabolic alkalosis, renin-angiotensin-aldosterone suppression, and edema. In studies and case reports, licorice toxicity has generally been completely reversible within one to several weeks of licorice discontinuation. However, renin-angiotensin-aldosterone axis may be suppressed for up to several months.', NULL, 'Patients receiving prolonged corticosteroid therapy should avoid or limit the consumption of licorice-containing products.', NULL, 'Patients receiving prolonged corticosteroid therapy should avoid or limit the consumption of licorice-containing products. Even relatively moderate doses of licorice may be problematic in susceptible patients when ingested regularly for prolonged periods.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75154/', '[1] Bannister B, Ginsburg R, Shneerson J "Cardiac arrest due to liquorice-induced hypokalaemia." Br Med J 2 (1977): 738-9[2] Richard CL, Jurgens TM "Effects of natural health products on blood pressure." Ann Pharmacother 39 (2005): 712-20[3] Edwards CR "Lessons from licorice." N Engl J Med 325 (1991): 1242-3[4] Sigurjons