INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75098, 'Tetracosactide', 'Nebivolol', 'Moderate', 'Corticosteroids may antagonize the effects of antihypertensive medications by inducing sodium and fluid retention. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) because they have greater mineralocorticoid activity. Conversely, some calcium channel blockers such as diltiazem and verapamil may increase corticosteroid plasma levels and effects by inhibiting their clearance via CYP450 3A4 metabolism.', NULL, 'Patients on prolonged (i.', NULL, 'Patients on prolonged (i.e., longer than about a week) or high-dose corticosteroid therapy should have blood pressure, electrolyte levels, and body weight monitored regularly, and be observed for the development of edema and congestive heart failure. The dosages of antihypertensive medications may require adjustment.', 'Metabolism, Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75185/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75099, 'Tetracosactide', 'Neostigmine', 'Moderate', 'Corticosteroids and adrenocorticotropic agents may diminish the therapeutic effects of acetylcholinesterase inhibitors in myasthenia gravis. The mechanism of interaction is unknown. Marked deterioration in muscle strength has been reported in patients with myasthenia gravis shortly after the initiation of corticosteroid therapy, particularly when high dosages were used.', NULL, 'Corticosteroid therapy should be instituted at relatively low dosages (15 to 25 mg/day of prednisone or equivalent) and in a controlled setting in patients with myasthenia gravis.', NULL, 'Corticosteroid therapy should be instituted at relatively low dosages (15 to 25 mg/day of prednisone or equivalent) and in a controlled setting in patients with myasthenia gravis. Respiratory support should be available, and the dosage should be increased stepwise as tolerated (approximately 5 mg/day of prednisone or equivalent at 2- to 3-day intervals until marked clinical improvement or a dosage of 50 mg/day is reached). Dose reductions of the acetylcholinesterase inhibitor may be required as symptoms improve, which often may be delayed and gradual.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75186/', '[1] Patten BM, Oliver KL, Engel WK "Adverse interaction between steroid hormones and anticholinesterase drugs." Neurology 24 (1974): 442-9[2] Brunner NG, Namba T, Grob D "Corticosteroids in management of severe, generalized myasthenia gravis." Neurology 22 (1972): 603-10[3] Namba T "Corticotropin therapy in patients with myasthenia gravis." Arch Neurol 26 (1972): 144-5