INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75138, 'Tetracosactide', 'Rotavirus vaccine', 'Moderate', 'The administration of live, attenuated virus or bacterial vaccines during corticosteroid therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive corticosteroid therapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive corticosteroid therapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored. The interval depends on the dosage and duration of corticosteroid therapy administered, but may be at least 3 months in most cases. Current local immunization guidelines should be consulted for recommendations. In patients who have recently been vaccinated, high-dose corticosteroid therapy should not be initiated for at least 2 weeks. Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75225/', '[1] "Product Information. Orimune (poliovirus vaccine, live, oral, trivalent)." Lederle Laboratories, Philadelphia, PA.[2] "Product Information. Attenuvax (measles virus vaccine, live, attenuated)." Merck & Co, Inc, West Point, PA.[3] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY