INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75264, 'Erlotinib', 'Verapamil', 'Moderate', 'Coadministration with an inhibitor of both CYP450 1A2 and 3A4 such as ciprofloxacin may significantly increase the plasma concentrations of erlotinib, which is a substrate of both isoenzymes.', NULL, 'Caution is advised if erlotinib must be used with inhibitors of both CYP450 1A2 and 3A4.', NULL, 'Caution is advised if erlotinib must be used with inhibitors of both CYP450 1A2 and 3A4. A dosage reduction or temporary interruption of therapy should be considered in patients who experience undue adverse effects of erlotinib such as severe diarrhea (i.e., that which is unresponsive to loperamide or results in dehydration), severe skin reactions, or severe liver function test abnormalities.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75351/', '[1] "Product Information. Tarceva (erlotinib)." Genentech, South San Francisco, CA.[2] Dimartini A "Isoniazid, tricyclics and the ''''cheese reaction''''." Int Clin Psychopharmacol 10 (1995): 197-8[3] "Product Information. INH (isoniazid)." Ciba Pharmaceuticals, Summit, NJ.[4] Smith CK, Durack DT "Isoniazid and reaction to cheese." Ann Intern Med 88 (1978): 520-1[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Self TH, Chrisman CR, Baciewicz AM, Bronze MS "Isoniazid drug and food interactions." Am J Med Sci 317 (1999): 304-11[7] Uragoda CG, Kottegoda SR "Adverse reactions to isoniazid on ingestion of fish with a high histamine content." Tubercle 58 (1977): 83-9[8] Li J, Zhao M, He P, Hidalgo M, Baker SD "Differential metabolism of gefitinib and erlotinib by human cytochrome p450