INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75288, 'Fluticasone', 'Verapamil', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the systemic exposure to fluticasone following intranasal administration or oral inhalation. Fluticasone undergoes extensive first-pass and systemic metabolism via CYP450 3A4, thus inhibition of the isoenzyme may significantly increase systemic bioavailability of the drug. However, the extent of interaction may depend on the route of fluticasone administration and the specific formulation.', NULL, 'Monitor for signs and symptoms of hypercorticism if intranasal or orally inhaled fluticasone is coadministered with a moderate inhibitor of CYP450 3A4.', NULL, 'Monitor for signs and symptoms of hypercorticism if intranasal or orally inhaled fluticasone is coadministered with a moderate inhibitor of CYP450 3A4. Signs and symptoms of hypercorticism include acne, striae, thinning of the skin, easy bruising, moon facies, dorsocervical "buffalo" hump, truncal obesity, increased appetite, acute weight gain, edema, hypertension, hirsutism, hyperhidrosis, proximal muscle wasting and weakness, glucose intolerance, exacerbation of preexisting diabetes, depression, and menstrual disorders. Other systemic glucocorticoid effects may include adrenal suppression, immunosuppression, posterior subcapsular cataracts, glaucoma, bone loss, and growth retardation in children and adolescents. Signs and symptoms of adrenal insufficiency include anorexia, hypoglycemia, nausea, vomiting, weight loss, muscle wasting, fatigue, weakness, dizziness, postural hypotension, depression, and adrenal crisis manifested as inability to respond to stress (e.g., illness, infection, surgery, trauma). Systemic glucocorticoids may be necessary until adrenal function recovers.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75375/', '[1] St Clair K, Maguire JD "Role of fluconazole in a case of rapid onset ritonavir and inhaled fluticasone-associated secondary adrenal insufficiency." Int J STD AIDS 23 (2012): 371-2[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Imbruvica (ibrutinib)." Pharmacyclics Inc, Sunnyvale, CA.[4] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Hoover WC, Britton LJ, Gardner J, Jackson T, Gutierrez H "Rapid onset of iatrogenic adrenal insufficiency in a patient with cystic fibrosis-related liver disease treated with inhaled corticosteroids and a moderate CYP3A4 inhibitor." Ann Pharmacother 45 (2011): e38[7] "Product Information. Bosulif (bosutinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75289, 'Fluvoxamine', 'Verapamil', 'Moderate', 'Coadministration with fluvoxamine may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to competitive inhibition of CYP450 3A4 activity by fluvoxamine.', NULL, 'Caution is advised if fluvoxamine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if fluvoxamine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fluvoxamine is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75376/', '[1] "Product Information. Luvox (fluvoxamine)." Solvay Pharmaceuticals Inc, Marietta, GA.[2] Damkier P, Hansen LL, Brosen K "Effect of fluvoxamine on the pharmacokinetics of quinidine." Eur J Clin Pharmacol 55 (1999): 451-6[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75290, 'Fosamprenavir', 'Verapamil', 'Moderate', 'Coadministration with amprenavir or its prodrug, fosamprenavir, may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by amprenavir.', NULL, 'Caution is advised if amprenavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if amprenavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever amprenavir is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75377/', '[1] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.[2] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[3] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.[4] Demarles D, Gillotin C, Bonaventure-Paci S, Vincent I, Fosse S, Taburet AM "Single-dose pharmacokinetics of amprenavir coadministered with grapefruit juice." Antimicrob Agents Chemother 46 (2002): 1589-1590', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75291, 'Fosaprepitant', 'Verapamil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors.', NULL, 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors. Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with aprepitant.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75378/', '[1] "Product Information. Emend for Injection (fosaprepitant)." Merck & Company Inc, West Point, PA.[2] "Product Information. Emend (aprepitant)." Merck & Company Inc, West Point, PA.', 1767365205);
