INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75407, 'Verapamil', 'Naldemedine', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of naldemedine, which is primarily metabolized by CYP450 3A4 to nor-naldemedine and to a minor extent by UGT1A3 to naldemedine 3-G. Both metabolites have demonstrated antagonistic activity for opioid receptors, but with less potency than the parent drug.', NULL, 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp).', NULL, 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp). Patients should be closely monitored for potential opioid withdrawal symptoms as well as other adverse effects of naldemedine.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75494/', '[1] "Product Information. Symproic (naldemedine)." Shionogi USA Inc, Florham Park, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Symproic (naldemedine)." Shionogi USA Inc, Florham Park, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75408, 'Verapamil', 'Naloxegol', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of naloxegol, which is primarily metabolized by the isoenzyme. Increased exposure to naloxegol may precipitate opioid withdrawal symptoms such as hyperhidrosis, lacrimation, rhinorrhea, chills, diarrhea, abdominal pain, anxiety, insomnia, irritability, restlessness, and yawning.', NULL, 'Concomitant use of naloxegol with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of naloxegol with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the dosage of naloxegol should be reduced to 12.5 mg once daily and the patient closely monitored for opioid withdrawal symptoms.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75495/', '[1] "Product Information. Movantik (naloxegol)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Movantik (naloxegol)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767365205);
