INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75456, 'Verapamil', 'Pitolisant', 'Moderate', 'Coadministration with pitolisant may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Pitolisant is a borderline/weak inducer of CYP450 3A4.', NULL, 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates.', NULL, 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates. Clinical and laboratory monitoring may be appropriate whenever pitolisant is added to or withdrawn from therapy, and dosage adjustments made if necessary. It may be advisable to avoid concomitant use of pitolisant and CYP450 3A4 substrates with a narrow therapeutic index, if possible.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75543/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Wakix (pitolisant)." Harmony Biosciences, LLC, Plymouth Meeting, PA.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75457, 'Verapamil', 'Polatuzumab vedotin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase exposure to unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of polatuzumab vedotin. MMAE is a substrate for CYP450 3A4.', NULL, 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors. Patients should be monitored for development or exacerbation of toxicities such as peripheral neuropathy, myelosuppression, opportunistic infections, tumor lysis syndrome and hepatotoxicity, and the dosing of polatuzumab vedotin adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75544/', '[1] "Product Information. Polivy (polatuzumab vedotin)." Genentech, South San Francisco, CA.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75458, 'Verapamil', 'Pomalidomide', 'Moderate', 'Coadministration with potent inhibitors of CYP450 1A2, CYP450 3A4, and P-glycoprotein may increase the plasma concentrations of pomalidomide, which has been shown to be primarily metabolized by these isoenzymes and also a substrate of the efflux transporter. Pomalidomide exposure is increased when given with a strong CYP450 1A2 inhibitor (e.g., fluvoxamine) in the presence of a strong CYP450 3A4 and P-gp inhibitor (e.g., ketoconazole). Coadministration with ketoconazole alone did not have a clinically significant effect on exposure to pomalidomide.', NULL, 'The use of pomalidomide with potent inhibitors of CYP450 1A2 (e.', NULL, 'The use of pomalidomide with potent inhibitors of CYP450 1A2 (e.g., ciprofloxacin, fluvoxamine, tiabendazole) in the presence of strong CYP450 3A4 and P-gp inhibitors should generally be avoided. If coadministration is considered clinically necessary, the pomalidomide dose should be reduced by 50%. Dose reduction may also be required if pomalidomide is given with a strong inhibitor of CYP450 1A2 in the absence of a coadministered CYP450 3A4 and P-gp inhibitor. Patients should be monitored for occurrence of pomalidomide-related side effects, including nausea, diarrhea, and neutropenia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75545/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Pomalyst (pomalidomide)." QLT Phototherapeutics Inc, Vancouver, AZ.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[6] "Product Information. Pomalyst (pomalidomide)." QLT Phototherapeutics Inc, Vancouver, AZ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75459, 'Verapamil', 'Ponatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ponatinib, which is a substrate of the isoenzyme.', NULL, 'Caution is advised if ponatinib is used in combination with moderate CYP450 3A4 inhibitors.', NULL, 'Caution is advised if ponatinib is used in combination with moderate CYP450 3A4 inhibitors. Patients should be monitored for potentially increased side effects such as thromboembolism, ischemia, congestive heart failure, hypertension, hepatotoxicity, pancreatitis, myelosuppression, hemorrhage, and fluid retention.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75546/', '[1] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 1767365205);
