    ) VALUES (1368797, 'Bisoprolol', 'Tetrofosmin', 'Moderate', 'Drugs such as beta-blockers, calcium channel blockers, and nitrates may influence myocardial function and blood flow.', NULL, 'Clinicians should consider concomitant medications that may influence myocardial function and blood flow when assessing images.', NULL, 'Clinicians should consider concomitant medications that may influence myocardial function and blood flow when assessing images.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
