        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1368799, 'Bisoprolol', 'Thallous Chloride', 'Moderate', 'Coadministration of certain drugs may alter the myocardial uptake of thallous chloride Tl-201 and interfere with results of myocardial imaging studies. Drugs that increase or decrease coronary blood flow or potassium uptake may alter the biodistribution of thallous chloride Tl-201.', NULL, 'Clinicians should be aware of possible diagnostic interference by these drugs.', NULL, 'Clinicians should be aware of possible diagnostic interference by these drugs.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
