    ) VALUES (1368829, 'Bisoprolol', 'Valsartan', 'Moderate', 'In the Valsartan Heart Failure Trial, the combination of valsartan with a beta-blocker and an ACE inhibitor was associated with unfavorable outcomes on morbidity and mortality in heart failure patients. The mechanism is unknown.', NULL, 'The manufacturer recommends that the triple combination of valsartan with a beta-blocker and an ACE inhibitor be avoided in heart failure patients.', NULL, 'The manufacturer recommends that the triple combination of valsartan with a beta-blocker and an ACE inhibitor be avoided in heart failure patients.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
