INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75502, 'Verapamil', 'Roflumilast', 'Moderate', 'Coadministration with CYP450 1A2 inhibitors or dual CYP450 1A2/3A4 inhibitors may increase the systemic exposure to roflumilast and its pharmacologically active N-oxide metabolite.', NULL, 'Treatment with roflumilast should be re-evaluated if an interaction is suspected and persistent intolerability occurs.', NULL, 'Treatment with roflumilast should be re-evaluated if an interaction is suspected and persistent intolerability occurs. Patients should be advised to contact their physician if they experience increased frequency and/or severity of side effects such as diarrhea, nausea, abdominal pain, weight loss, and headache.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75589/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Daxas (roflumilast)." Nycomed Inc, Princeton, NJ.[3] "Product Information. Daliresp (roflumilast)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Daxas (roflumilast)." Nycomed Inc, Princeton, NJ.[6] "Product Information. Daliresp (roflumilast)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75503, 'Verapamil', 'Romidepsin', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of romidepsin, which is a substrate of the efflux transporter. Although there are no formal drug interaction studies, the interaction may increase the risk of hematologic toxicities such as anemia, leukopenia, and thrombocytopenia as well as electrocardiographic changes such as QT interval prolongation and T-wave and ST-segment changes.', NULL, 'Caution is advised if romidepsin is prescribed in combination with P-gp inhibitors.', NULL, 'Caution is advised if romidepsin is prescribed in combination with P-gp inhibitors. Pharmacologic response to romidepsin should be monitored more closely whenever a P-gp inhibitor is added to or withdrawn from therapy, and the romidepsin dosage adjusted as necessary. Patients should have complete blood cell counts, electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75590/', '[1] "Product Information. Istodax (romidepsin)." Gloucester Pharmaceuticals, Cambridge, MA.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75504, 'Verapamil', 'Ropivacaine', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may modestly increase the plasma concentrations of ropivacaine. Although ropivacaine is primarily metabolized by CYP450 1A2, it has been shown to undergo some metabolism via CYP450 3A4.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75591/', '[1] Jokinen MJ, Ahonen J, Neuvonen PJ, Olkkola KT "The effect of erythromycin, fluvoxamine, and their combination on the pharmacokinetics of ropivacaine." Anesth Analg 91 (2000): 1207-12[2] Ekstrom G, Gunnarsson UB "Ropivacaine, a new amide-type local anesthetic agent, is metabolized by cytochromes P450 1A and 3A in human liver microsomes." Drug Metab Dispos 24 (1996): 955-61[3] Arlander E, Ekstrom G, Alm C, Carrillo JA, Bielenstein M, Bottiger Y, Bertilsson L, Gustafsson LL "Metabolism of ropivacaine in humans is mediated by CYP1A2 and to a minor extent by CYP3A4: An interaction study with fluvoxamine and ketoconazole as in vivo inhibitors." Clin Pharmacol Ther 64 (1998): 484-91[4] McClure JH "Ropivacaine." Br J Anaesth 76 (1996): 300-7[5] "Product Information. Naropin (ropivacaine)." Astra USA, Westborough, MA.[6] Halldin MM, Bredberg E, Angelin B, Arvidsson T, Askemark Y, Elofsson S, Widman M "Metabolism and excretion of ropivacaine in humans." Drug Metab Dispos 24 (1996): 962-8[7] Oda Y, Furuichi K, Tanaka K, Hiroi T, Imaoka S, Asada A, Fujimori M, Funae Y "Metabolism of a new local anesthetic, ropivacaine, by human hepatic cytochrome P450." Anesthesiology 82 (1995): 214-20', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75505, 'Verapamil', 'Rucaparib', 'Moderate', 'Coadministration with potent inhibitors of P-glycoprotein (P-gp) may increase the plasma concentration and the risk of adverse effects of rucaparib, which has been shown to be a substrate of P-gp. However, the effects on the pharmacokinetics of rucaparib have not been evaluated in vivo.', NULL, 'Caution is advised if rucaparib is used concomitantly with potent P-gp inhibitors.', NULL, 'Caution is advised if rucaparib is used concomitantly with potent P-gp inhibitors. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever a potent P-gp inhibitor is added to or withdrawn from therapy with rucaparib.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75592/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75506, 'Verapamil', 'Ruxolitinib', 'Minor', 'Coadministration with mild or moderate inhibitors of CYP450 3A4 may modestly increase the plasma concentrations of ruxolitinib, which is primarily metabolized by the isoenzyme. No dosage adjustment is recommended when ruxolitinib is coadministered with mild or moderate CYP450 3A4 inhibitors.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75593/', '[1] "Product Information. Jakafi (ruxolitinib)." Incyte Corporation, Wilmington, DE.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Jakafi (ruxolitinib)." Incyte Corporation, Wilmington, DE.', 1767365205);
