INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75540, 'Verapamil', 'Tacrine', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75627/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Cognex (tacrine)." Parke-Davis, Morris Plains, NJ.[3] Tiseo PJ, Perdomo CA, Friedhoff LT "Concurrent administration of donepezil HCI and digoxin: assessment of pharmacokinetic changes." Br J Clin Pharmacol 46(Suppl 1) (1998): 40-4[4] Tavassoli N, Sommet A, Lapeyre-Mestre M, Bagheri H, Montrastruc JL "Drug interactions with cholinesterase inhibitors : an analysis 