INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75668, 'Disulfiram', 'Crizotinib', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75755/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Pharmaceutical Society of Australia "APPGuide online. Australian prescription products guide online. Available from: URL: http://www.appco.com.au/appguide/default.asp." ([2006]):[3] Carrion C, Espinosa E, Herrero A, Garcia B "Possible vincristine-isoniazid interaction." Ann Pharmacother 29 (1995): 201[4] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979): 663-6', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75669, 'Docetaxel', 'Crizotinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 or dual CYP450 3A4 and P-glycoprotein (P-gp) inhibitors may significantly increase the plasma concentrations of docetaxel, which is a substrate of both CYP450 3A4 and P-gp.', NULL, 'Concomitant use of docetaxel with CYP450 3A4 inhibitors, particularly potent inhibitors or dual CYP450 3A4 and P-gp inhibitors such as itraconazole, ketoconazole, posaconazole, conivaptan, ceritinib, idelalisib, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics, should generally be avoided.', NULL, 'Concomitant use of docetaxel with CYP450 3A4 inhibitors, particularly potent inhibitors or dual CYP450 3A4 and P-gp inhibitors such as itraconazole, ketoconazole, posaconazole, conivaptan, ceritinib, idelalisib, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics, should generally be avoided. Some authorities recommend avoiding concomitant use of docetaxel during and for 2 weeks after treatment with itraconazole. If concomitant use is required, a reduced dosage of docetaxel should be considered. Based on extrapolation from the pharmacokinetic study in 7 cancer patients, a 50% reduction in the docetaxel dose may be reasonable. Patients should be closely monitored for the development of docetaxel toxicity such as myelosuppression, stomatitis, neurotoxicity (e.g., paraesthesia, dysesthesia, pain), myalgia, asthenia, fluid retention, nausea, vomiting, and diarrhea.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/757