INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75687, 'Eletriptan', 'Crizotinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eletriptan, which is primarily metabolized by the isoenzyme.', NULL, 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, nefazodone, delavirdine, most protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, nefazodone, delavirdine, most protease inhibitors, and ketolide and certain macrolide antibiotics. Patients should have vital signs monitored regularly and advised to notify their physician if they experience signs and symptoms of vasospasm such as numbness, tingling, or cyanosis in the extremities; muscle pains; weakness; or chest pain or tightness. Alternatively, other 5-HT1 receptor agonists that are not metabolized by CYP450 3A4 may be considered, such as frovatriptan, naratriptan, rizatriptan, sumatriptan, and zolmitriptan.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75774/', '[1] "Product Information. Relpax (eletriptan)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75688, 'Eliglustat', 'Crizotinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', NULL, 'The use of eliglustat in combination with one or more drugs that may result in moderate inhibition of CYP450 3A4 and moderate to potent inhibition of CYP450 2D6 is considered contraindicated in CYP450 2D6 extensive metabolizers (EMs) and intermediate metabolizers (IMs).', NULL, 'The use of eliglustat in combination with one or more drugs that may result in moderate inhibition of CYP450 3A4 and moderate to potent inhibition of CYP450 2D6 is considered contraindicated in CYP450 2D6 extensive metabolizers (EMs) and intermediate metabolizers (IMs). In the absence of a concomitant CYP450 2D6 inhibitor, eliglustat may be prescribed at a reduced dosage of 84 mg once daily to EMs treated with a moderate CYP450 3A4 inhibitor. However, eliglustat should not be used with a moderate CYP450 3A4 inhibitor in CYP450 2D6 IMs or poor metabolizers (PMs). Moderate CYP450 3A4 inhibitors include aprepitant, ciprofloxacin, clotrimazole, crizotinib, darunavir, diltiazem, dronedarone, fluconazole, fusidic acid, imatinib, isavuconazonium, miconazole, mifepristone, netupitant, quinupristin-dalfopristin, ranolazine, stiripentol, and verapamil. Potent and moderate CYP450 2D6 inhibitors include abiraterone, bupropion, celecoxib, cimetidine, cinacalcet, clobazam, darifenacin, diphenhydramine, duloxetine, fluoxetine, methotrimeprazine, mirabegron, paroxetine, propoxyphene, quinidine, ranolazine, sertraline, stiripentol, and terbinafine.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75775/', '[1] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.[2] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75689, 'Elotuzumab', 'Crizotinib', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75776/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Pharmaceutical Society of Australia "APPGuide online. Australian prescription products guide online. Available from: URL: http://www.appco.com.au/appguide/default.asp." ([2006]):[3] Carrion C, Espinosa E, Herrero A, Garcia B "Possible vincristine-isoniazid interaction." Ann Pharmacother 29 (1995): 201[4] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979): 663-6', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75690, 'Encorafenib', 'Crizotinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of encorafenib, which is primarily metabolized by the isoenzyme. Increased exposure to encorafenib may increase the risk of serious and life-threatening adverse effects such as hemorrhage, uveitis, QT prolongation, hepatotoxicity, dermatologic reactions, and new malignancies.', NULL, 'Concomitant use of encorafenib with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of encorafenib with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing the encorafenib dose to one-half of the dose used prior to addition of the moderate CYP450 3A4 inhibitor. After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the encorafenib dose that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75777/', '[1] "Product Information. Braftovi (encorafenib)." Array BioPharma Inc., Boulder, CO.[2] "Product Information. Braftovi (encorafenib)." Array BioPharma Inc., Boulder, CO.[3] "Product Information. Braftovi (encorafenib)." Array BioPharma Inc., Boulder, CO.', 1767365205);
