INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75709, 'Etravirine', 'Crizotinib', 'Moderate', 'Coadministration of etravirine with a drug that is both a substrate as well as inhibitor of CYP450 3A4 may result in increased plasma concentrations of etravirine and decreased plasma concentrations of the other drug. Etravirine itself is a substrate and inducer of CYP450 3A4. Theoretically, etravirine may induce metabolism of the coadministered drug while its own metabolism may be inhibited by the coadministered drug.', NULL, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with a drug that is an inhibitor of CYP450 3A4.', NULL, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with a drug that is an inhibitor of CYP450 3A4. In addition, dosage adjustments may be required for the coadministered drug if it is also a substrate of CYP450 3A4.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75796/', '[1] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.[2] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75710, 'Everolimus', 'Crizotinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 and P-glycoprotein may significantly increase the plasma concentrations of everolimus following oral administration. Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus. The risk of side effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycaemia, and hyperlipidemia may be increased.', NULL, 'Blood trough levels should be closely monitored, and the dosage adjusted accordingly during concomitant administration and after discontinuation of moderate CYP450 3A4 and P-glycoprotein inhibitors.', NULL, 'Blood trough levels should be closely monitored, and the dosage adjusted accordingly during concomitant administration and after discontinuation of moderate CYP450 3A4 and P-glycoprotein inhibitors. The manufacturer''s drug product labeling should be consulted for specific recommendations.', 'Metabolism, Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75797/', '[1] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75711, 'Ezogabine', 'Crizotinib', 'Major', 'Crizotinib can cause concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if crizotinib is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if crizotinib is used in combination with other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting crizotinib therapy and periodically during treatment.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75798/', '[1] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365205);
