INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75770, 'Istradefylline', 'Crizotinib', 'Moderate', 'Coadministration with crizotinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by crizotinib. The interaction has been studied with midazolam, a probe substrate for CYP450 3A4.', NULL, 'Caution is advised when crizotinib is used concomitantly with drugs that undergo metabolism by CYP450 3A4.', NULL, 'Caution is advised when crizotinib is used concomitantly with drugs that undergo metabolism by CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever crizotinib is added to or withdrawn from therapy. Concomitant use of crizotinib and substrates of CYP450 3A4 with a narrow therapeutic index (e.g., antiarrhythmic agents, benzodiazepines, ergot derivatives, immunosuppressants, vinca alkaloids) should be avoided if possible.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75857/', '[1] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365205);
