INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75960, 'Crizotinib', 'Selegiline', 'Moderate', 'Coadministration with crizotinib may theoretically increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 2B6 (e.g., bupropion), P-glycoprotein (P-gp) (e.g., dabigatran), and/or the organic cation transporters 1 and 2 (OCT1, OCT2) (e.g., metformin). The proposed mechanism, based on in vitro data, is decreased clearance due to crizotinib-mediated inhibition of CYP450 2B6, P-gp, and/or OCT1 or OCT2.', NULL, 'Although the clinical significance is unknown, caution is advised if crizotinib is used concomitantly with drugs that are substrates of CYP450 2B6, P-gp, and/or OCT1 or OCT2, particularly those with a narrow therapeutic range.', NULL, 'Although the clinical significance is unknown, caution is advised if crizotinib is used concomitantly with drugs that are substrates of CYP450 2B6, P-gp, and/or OCT1 or OCT2, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever crizotinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76047/', '[1] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[5] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (75961, 'Crizotinib', 'Selpercatinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of selpercatinib, which is primarily metabolized by the isoenzyme. Increased exposure to selpercatinib may increase the risk of serious adverse effects such as QT interval prolongation, liver transaminase and bilirubin elevations, hypertension, hemorrhage, edema, and hypersensitivity reactions (e.g., fever, rash, arthralgias/myalgias with concurrent decreased platelets or transaminitis).', NULL, 'Concomitant use of selpercatinib with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of selpercatinib with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends decreasing the dosage of selpercatinib. Patients receiving selpercatinib 120 mg twice daily should have the dosage reduced to 80 mg twice daily, and those receiving 160 mg twice daily should have the dosage reduced to 120 mg twice daily. Close monitoring for adverse effects is advisable, including more frequent ECGs and laboratory monitoring of liver enzymes, bilirubin, electrolytes, glucose, and blood counts. Selpercatinib treatment should be discontinued, interrupted, or dosage reduced in patients with serious or life-threatening toxicities in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76048/', '[1] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Retevmo (selpercatinib).