INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369023, 'Indium In-111 pentetreotide', 'Bleomycin', 'Moderate', 'Indium In-111 pentetreotide may accumulate in the lungs when given concomitantly with bleomycin.', 'DDInter', 1767317475, 'Clinicians should be aware of possible diagnostic interference in patients who are undergoing bleomycin treatment. To help reduce the radiation dose to the lungs and other target organs (e.g., thyroid, kidneys, bladder), patients should be well hydrated prior to the administration of indium In-111 pentetreotide. They should increase fluid intake and void frequently for one day after the scintigraphy. In addition, it is recommended that patients be given a mild laxative (e.g., bisacodyl or lactulose) before and after administration of indium In-111 pentetreotide.', 'Others', 'Clinicians should be aware of possible diagnostic interference in patients who are undergoing bleomycin treatment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
