INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369128, 'Deferiprone', 'Blinatumomab', 'Major', 'Coadministration of deferiprone and other drugs that can cause neutropenia or agranulocytosis may increase the risk and/or severity of hematologic toxicity. Deferiprone alone can cause agranulocytosis, which may be preceded by neutropenia.', 'DDInter', 1767317475, 'Concomitant use of deferiprone with other drugs known to be associated with neutropenia or agranulocytosis should generally be avoided. Otherwise, close monitoring of absolute neutrophil count (ANC) is recommended if coadministration is required.', 'Synergism', 'Concomitant use of deferiprone with other drugs known to be associated with neutropenia or agranulocytosis should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369129, 'Denosumab', 'Blinatumomab', 'Moderate', 'Concomitant use of immunosuppressive or myelosuppressive agents with denosumab may increase the risk of serious infections. Denosumab binds to and inhibits the receptor activator of nuclear factor kappa-B ligand (RANKL), which is expressed on activated T and B lymphocytes and in lymph nodes. Thus, denosumab alone may increase the risk of infections.', 'DDInter', 1767317475, 'Caution is advised if denosumab must be used in combination with immuno- or myelosuppressive agents. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination. The need for continued denosumab therapy should be assessed when serious infections occur during treatment.', 'Synergism', 'Caution is advised if denosumab must be used in combination with immuno- or myelosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
