INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369261, 'Erdafitinib', 'Calcium Phosphate', 'Major', 'Coadministration with agents that can alter serum phosphate levels may affect the initial dosage determination of erdafitinib. The mechanism appears to be related to the pharmacodynamic effects of fibroblast growth factor receptor (FGFR) inhibition by erdafitinib. Inhibition of the FGFR receptor has been shown to lead to an increase in serum phosphate levels.', 'DDInter', 1767317475, 'The manufacturer recommends that concomitant use of erdafitinib with agents that may alter serum phosphate levels be avoided before its initial dose increase period, which is between days 14 to 21 of starting erdafitinib therapy and is based on serum phosphate levels. After the initial dose increase period, an oral phosphate binder may be appropriate in the management of patients with serum phosphate levels greater than 7 mg/dL. If concomitant use with agents known to increase serum phosphate levels is required after this initial dose increase period, close monitoring of patient tolerability and serum phosphate levels is recommended.', 'Others', 'The manufacturer recommends that concomitant use of erdafitinib with agents that may alter serum phosphate levels be avoided before its initial dose increase period, which is between days 14 to 21 of starting erdafitinib therapy and is based on serum phosphate levels.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369262, 'Folic acid', 'Eslicarbazepine', 'Moderate', 'Anticonvulsants including, but not limited to, carbamazepine, lamotrigine, phenobarbital, phenytoin, primidone, and valproic acid have been shown to impair folate absorption and increase the metabolism of circulating folate. Coadministration with folate therapy may reduce the anticonvulsant effects of phenytoin, phenobarbital, primidone, and succinimides. The exact mechanism of interaction is unknown. Available data pertain primarily to phenytoin.', 'DDInter', 1767317475, 'Caution is advised when folate therapy is coadministered with anticonvulsants. Close monitoring for clinical and laboratory evidence of diminished therapeutic response to both treatments is recommended. Patients should be advised to notify their physician if they experience loss of seizure control.', 'Antagonism', 'Caution is advised when folate therapy is coadministered with anticonvulsants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
