INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369280, 'Isradipine', 'Calcium Phosphate', 'Moderate', 'Calcium-containing products may decrease the effectiveness of calcium channel blockers by saturating calcium channels with calcium. Calcium chloride has been used to manage acute severe verapamil toxicity.', 'DDInter', 1767317475, 'Management consists of monitoring the effectiveness of calcium channel blocker therapy during coadministration with calcium products.', 'Antagonism', 'Management consists of monitoring the effectiveness of calcium channel blocker therapy during coadministration with calcium products.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369281, 'Calcium Phosphate', 'Labetalol', 'Moderate', 'INTERVAL: Concurrent administration with calcium salts may decrease the oral bioavailability of atenolol and possibly other beta-blockers. The exact mechanism of interaction is unknown. The investigators suggest that prolongation of the elimination half-life induced by calcium coadministration may have led to atenolol cumulation during long-term dosing, which compensated for the reduced bioavailability.', 'DDInter', 1767317475, 'It may help to separate the administration times of beta-blockers and calcium products by at least 2 hours. Patients should be monitored for potentially diminished beta-blocking effects following the addition of calcium therapy.', 'Absorption', 'It may help to separate the administration times of beta-blockers and calcium products by at least 2 hours.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369282, 'Folic acid', 'Lamotrigine', 'Moderate', 'Anticonvulsants including, but not limited to, carbamazepine, lamotrigine, phenobarbital, phenytoin, primidone, and valproic acid have been shown to impair folate absorption and increase the metabolism of circulating folate. Coadministration with folate therapy may reduce the anticonvulsant effects of phenytoin, phenobarbital, primidone, and succinimides. The exact mechanism of interaction is unknown. Available data pertain primarily to phenytoin.', 'DDInter', 1767317475, 'Caution is advised when folate therapy is coadministered with anticonvulsants. Close monitoring for clinical and laboratory evidence of diminished therapeutic response to both treatments is recommended. Patients should be advised to notify their physician if they experience loss of seizure control.', 'Antagonism', 'Caution is advised when folate therapy is coadministered with anticonvulsants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
