INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369285, 'Pyridoxine', 'Levodopa', 'Moderate', 'Pyridoxine doses of 5 mg/day or more antagonise the effects of levodopa. In the presence of pyridoxine, the peripheral metabolism of levodopa to dopamine is increased, resulting in less levodopa available to enter the CNS. The dopa-decarboxylase inhibitors carbidopa and benserazide reduce the peripheral metabolism of levodopa, even in the presence of high concentrations of pyridoxine, allowing more levodpa to enter the CNS.', 'DDInter', 1767317475, 'The concomitant use of pyridoxine and levodopa without dopa-decarboxylase inhibitors should be avoided. Patients should be advised against taking pyridoxine-containing vitamin supplements.', 'Antagonism', 'The concomitant use of pyridoxine and levodopa without dopa-decarboxylase inhibitors should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
