INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369486, 'Ibandronate', 'Zinc acetate', 'Moderate', 'INTERVAL: Products containing aluminum, calcium, magnesium and other polyvalent cations such as antacids or vitamin with mineral supplements are likely to interfere with the gastrointestinal absorption of oral bisphosphonates.', 'DDInter', 1767317475, 'Antacids or other oral medications containing aluminum, calcium, magnesium and other polyvalent cations should be administered at least 30 minutes after the bisphosphonate dose.', 'Absorption', 'Antacids or other oral medications containing aluminum, calcium, magnesium and other polyvalent cations should be administered at least 30 minutes after the bisphosphonate dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
