INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369489, 'Bortezomib', 'Acetohexamide', 'Moderate', 'Bortezomib may interfere with the therapeutic effects of insulin and other antidiabetic agents.', 'DDInter', 1767317475, 'Blood glucose should be closely monitored when bortezomib is prescribed to patients receiving insulin or other antidiabetic agents. Patients should learn to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypo- or hyperglycemia occur during bortezomib therapy, patients should initiate appropriate remedial therapy immediately and contact their physician.', 'Others', 'Blood glucose should be closely monitored when bortezomib is prescribed to patients receiving insulin or other antidiabetic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369490, 'Human adenovirus e serotype 4 strain cl-68578 antigen', 'Bortezomib', 'Moderate', 'There are no data regarding the use of live, unattenuated Adenovirus Type 4 and Type 7 vaccine during treatment with immunosuppressive therapies, including alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. It is not known whether immunosuppressed patients may have an increased risk of adverse reactions or a decreased or suboptimal immunologic response to the vaccine.', 'DDInter', 1767317475, 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies. Caution is advised.', 'Others', 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369491, 'Aminoglutethimide', 'Bortezomib', 'Minor', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of bortezomib, which is primarily metabolized by the isoenzyme with secondary contribution from CYP450 2C19.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369492, 'Amprenavir', 'Bortezomib', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of bortezomib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Pharmacologic response to bortezomib should be monitored more closely whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary. Patients should be monitored for the development of potential bortezomib toxicities such as peripheral neuropathy, orthostatic hypotension, thrombocytopenia, and neutropenia.', 'Metabolism', 'Pharmacologic response to bortezomib should be monitored more closely whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369493, 'Bortezomib', 'Aurothioglucose', 'Moderate', 'Bortezomib can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk and/or severity of nerve damage. Bortezomib treatment causes a peripheral neuropathy that is predominantly sensory, although cases of mixed sensorimotor neuropathy have also been reported.', 'DDInter', 1767317475, 'Caution is advised if bortezomib is used with other neurotoxic agents. Patients should be closely monitored for symptoms of neuropathy such as visual disturbances or burning, tingling, pain, numbness, and/or weakness in the extremities. Patients experiencing new or worsening peripheral neuropathy may require an adjustment in the dosage and schedule of bortezomib in accordance with the product labeling.', 'Synergism', 'Caution is advised if bortezomib is used with other neurotoxic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369494, 'Bortezomib', 'Bacillus calmette-guerin substrain tice live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 1767317475, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369495, 'Bortezomib', 'Bedaquiline', 'Moderate', 'Coadministration of bedaquiline with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', 'DDInter', 1767317475, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; other antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; ketolide and macrolide antibiotics; interferons; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), especially in patients with diminished hepatic reserve. Patients treated with bedaquiline should have serum ALT, AST, alkaline phosphatase, and bilirubin monitored at baseline and monthly during treatment, or as often as needed. An increase of serum aminotransferases to greater than 3 times ULN should be followed by repeat testing within 48 hours. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Discontinue bedaquiline if aminotransferase elevations are accompanied by total bilirubin elevation greater than 2 times ULN, aminotransferase elevations are greater than 8 times ULN, or aminotransferase elevations persist beyond 2 weeks.', 'Synergism', 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369496, 'Bortezomib', 'Benznidazole', 'Moderate', 'Bortezomib can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk and/or severity of nerve damage. Bortezomib treatment causes a peripheral neuropathy that is predominantly sensory, although cases of mixed sensorimotor neuropathy have also been reported.', 'DDInter', 1767317475, 'Caution is advised if bortezomib is used with other neurotoxic agents. Patients should be closely monitored for symptoms of neuropathy such as visual disturbances or burning, tingling, pain, numbness, and/or weakness in the extremities. Patients experiencing new or worsening peripheral neuropathy may require an adjustment in the dosage and schedule of bortezomib in accordance with the product labeling.', 'Synergism', 'Caution is advised if bortezomib is used with other neurotoxic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369497, 'Bortezomib', 'Bifidobacterium longum infantis', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy. It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed.', 'Others', 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
