INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369539, 'Dapagliflozin', 'Bortezomib', 'Moderate', 'Bortezomib may interfere with the therapeutic effects of insulin and other antidiabetic agents.', 'DDInter', 1767317475, 'Blood glucose should be closely monitored when bortezomib is prescribed to patients receiving insulin or other antidiabetic agents. Patients should learn to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypo- or hyperglycemia occur during bortezomib therapy, patients should initiate appropriate remedial therapy immediately and contact their physician.', 'Others', 'Blood glucose should be closely monitored when bortezomib is prescribed to patients receiving insulin or other antidiabetic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
