INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369700, 'Bortezomib', 'Radium Ra 223 dichloride', 'Moderate', 'The concomitant use of bone marrow depressants and radium Ra 223 dichloride may have additive myelosuppressive effects.', 'DDInter', 1767317475, 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride. Dose reductions of the other bone marrow depressants may be necessary. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms. The safety and efficacy of concomitant use of chemotherapy have not been established.', 'Synergism', 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369701, 'Bortezomib', 'Remdesivir', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', 'DDInter', 1767317475, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369702, 'Bortezomib', 'Repaglinide', 'Moderate', 'Bortezomib may interfere with the therapeutic effects of insulin and other antidiabetic agents.', 'DDInter', 1767317475, 'Blood glucose should be closely monitored when bortezomib is prescribed to patients receiving insulin or other antidiabetic agents. Patients should learn to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypo- or hyperglycemia occur during bortezomib therapy, patients should initiate appropriate remedial therapy immediately and contact their physician.', 'Others', 'Blood glucose should be closely monitored when bortezomib is prescribed to patients receiving insulin or other antidiabetic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369703, 'Bortezomib', 'Rifabutin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of bortezomib, which is primarily metabolized by the isoenzyme with secondary contribution from CYP450 2C19.', 'DDInter', 1767317475, 'Given the potential for diminished pharmacologic effects of bortezomib in the presence of potent CYP450 3A4 inducers, concomitant use is not recommended.', 'Metabolism', 'Given the potential for diminished pharmacologic effects of bortezomib in the presence of potent CYP450 3A4 inducers, concomitant use is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
