INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369739, 'Bortezomib', 'Telaprevir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of bortezomib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Pharmacologic response to bortezomib should be monitored more closely whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary. Patients should be monitored for the development of potential bortezomib toxicities such as peripheral neuropathy, orthostatic hypotension, thrombocytopenia, and neutropenia.', 'Metabolism', 'Pharmacologic response to bortezomib should be monitored more closely whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
