INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369840, 'Dicoumarol', 'Bosentan', 'Moderate', 'Coadministration with bosentan may decrease the plasma concentrations of warfarin and other oral anticoagulants. The mechanism is bosentan induction of CYP450 2C9 and 3A4, two isoenzymes that are responsible for the metabolic clearance of warfarin.', 'DDInter', 1767317475, 'Patients should be closely monitored during concomitant therapy with oral anticoagulants and bosentan. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation, or change of dosage of bosentan in patients who are stabilized on their anticoagulant regimen.', 'Metabolism', 'Patients should be closely monitored during concomitant therapy with oral anticoagulants and bosentan.', NULL, NULL, 'Moderate', 'pharmacodynamic');
