INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369847, 'Doravirine', 'Bosentan', 'Major', 'Coadministration with moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of doravirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of doravirine with moderate CYP450 3A4 inducers should generally be avoided. If coadministration is necessary, some authorities recommend that the doravirine dose be increased to 100 mg twice daily (approximately 12 hours apart).', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of doravirine with moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
