INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76501, 'Carteolol', 'Phenylephrine (ophthalmic)', 'Moderate', 'A case report suggests that beta-blockers may enhance the pressor response to phenylephrine. The proposed mechanism involves blockade of beta-2 adrenergic receptors in the peripheral vasculature, resulting in unopposed alpha-adrenergic effect of phenylephrine that is responsible for vasoconstriction. Additionally, beta-blockers may desensitize baroreceptors that normally modulate heart rate in response to blood pressure elevations by increasing vagal activity on the sinoauricular node.', NULL, 'Until more information is available, caution should be exercised when phenylephrine is used in combination with beta-blockers including ophthalmic formulations, which may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', NULL, 'Until more information is available, caution should be exercised when phenylephrine is used in combination with beta-blockers including ophthalmic formulations, which may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Monitoring of blood pressure should be considered, particularly when phenylephrine is administered intravenously or intraocularly.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76602/', '[1] Myers MG, Iazzetta JJ "Intranasally administered phenylephrine and blood pressure." Can Med Assoc J 127 (1982): 365-6[2] Cass E, Kadar D, Stein HA "Hazards of phenylephrine topical medication in persons taking propranolol." Can Med Assoc J 120 (1979): 1261-2', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76502, 'Deserpidine', 'Phenylephrine (ophthalmic)', 'Moderate', 'Sympathomimetic amines may decrease the hypotensive effect of postganglionic adrenergic blocking agents, while the latter may potentiate the pharmacologic effects of direct-acting sympathomimetic amines (e.g., dobutamine, epinephrine, methoxamine, norepinephrine) but inhibit those that are primarily indirect-acting (e.g., mephentermine). Postganglionic adrenergic blocking agents such as guanadrel, guanethidine, and rauwolfia alkaloids work by depleting catecholamine stores from adrenergic nerve endings. Therefore, they may sensitize adrenergic receptors to direct-acting sympathomimetics, but blunt the effects of indirect-acting agents whose activity is mediated through the release of catecholamines.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension. Alternatives to postganglionic adrenergic blocking agents should be considered if patients are treated with sympathomimetic amines, since effects of the latter may be intensified or diminished depending on whether they are direct- or indirect-acting. Most agents with indirect sympathomimetic activity are mixed-acting, thus it may be difficult to predict how they will be affected by postganglionic adrenergic blocking agents. If the combination is used, blood pressure and heart rate should be monitored.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76603/', '[1] Gulati OD, Dave BT, Gokhale SD, Shah KM "Antagonism of adrenergic neuron blockade in hypertensive subjects." Clin Pharmacol Ther 7 (1966): 510-4[2] Ziegler CH, Lovette JB "Operative complications after therapy with reserpine and reserpine compounds." JAMA 176 (1961): 114-7[3] Spiers AS, Calne DB "Action of dopamine on the human iris." Br Med J 4 (1969): 333-5[4] Muelheims GH, Entrup RW, Paiewonsky D, Mierzwiak DS "Increased sensitivity of the heart to catecholamine-induced arrhythmias following guanethidine." Clin Pharmacol Ther 6 (1965): 757-62[5] Sherman GP, Walton CA "Adrenergic transmission and drug interaction." J Am Pharm Assoc 15 (1975): 86-90[6] Hardman JG, Gilman AG, Limbird LE eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 9th ed." New York, NY: McGraw-Hill (1995):[7] Sneddon JM, Turner P "Ephedrine mydriasis in hypertension and the response to treatment." Clin Pharmacol Ther 10 (1969): 64-71[8] Burn JH, Rand MJ "The action of sympathomimetic amines in animals treated with reserpine." J Physiol (Lond) 144 (1958): 314-36', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76503, 'Desvenlafaxine', 'Phenylephrine (ophthalmic)', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. The use of SNRIs alone has been associated with sustained increases in blood pressure and heart rate, and cases of elevated blood pressure requiring immediate treatment have been reported in postmarketing experience.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter. Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76604/', '[1] "Product Information. Fetzima (levomilnacipran)." Forest Pharmaceuticals, St. Louis, MO.[2] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals, Raritan, NJ.[4] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Pristiq (desvenlafaxine)." Wyeth Laboratories, Philadelphia, PA.[6] "Product Information. S