INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76559, 'Solriamfetol', 'Phenylephrine (ophthalmic)', 'Moderate', 'Additive or synergistic effects on blood pressure and/or heart rate may occur when solriamfetol is combined with other drugs that increase blood pressure and/or heart rate. The use of solriamfetol alone has been associated with dose-dependent increases in blood pressure and heart rate.', NULL, 'Caution is advised if solriamfetol is used with other drugs that increase blood pressure and/or heart rate.', NULL, 'Caution is advised if solriamfetol is used with other drugs that increase blood pressure and/or heart rate. Blood pressure should be monitored prior to initiating solriamfetol therapy and regularly during treatment. In addition, hypertension should be controlled prior to treatment, and any new-onset hypertension or exacerbations of pre-existing hypertension that occur during solriamfetol therapy should be treated. Discontinuation of solriamfetol should be considered in patients who experience an increase in blood pressure or heart rate that cannot be managed with dose reduction of solriamfetol or other appropriate medical intervention.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76660/', '[1] "Product Information. Sunosi (solriamfetol)." Jazz Pharmaceuticals, Palo Alto, CA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76560, 'Sotalol', 'Phenylephrine (ophthalmic)', 'Moderate', 'A case report suggests that beta-blockers may enhance the pressor response to phenylephrine. The proposed mechanism involves blockade of beta-2 adrenergic receptors in the peripheral vasculature, resulting in unopposed alpha-adrenergic effect of phenylephrine that is responsible for vasoconstriction. Additionally, beta-blockers may desensitize baroreceptors that normally modulate heart rate in response to blood pressure elevations by increasing vagal activity on the sinoauricular node.', NULL, 'Until more information is available, caution should be exercised when phenylephrine is used in combination with beta-blockers including ophthalmic formulations, which may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', NULL, 'Until more information is available, caution should be exercised when phenylephrine is used in combination with beta-blockers including ophthalmic formulations, which may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Monitoring of blood pressure should be considered, particularly when phenylephrine is administered intravenously or intraocularly.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76661/', '[1] Myers MG, Iazzetta JJ "Intranasally administered phenylephrine and blood pressure." Can Med Assoc J 127 (1982): 365-6[2] Cass E, Kadar D, Stein HA "Hazards of phenylephrine topical medication in persons taking propranolol." Can Med Assoc J 120 (1979): 1261-2'