INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76660, 'Cyclophosphamide', 'Modafinil', 'Minor', 'The concomitant use of modafinil may theoretically alter the metabolic activation and inactivation of ifosfamide and cyclophosphamide. Modafinil is a modest CYP450 2B6 and 3A4 inducer in vitro. Ifosfamide and cyclophosphamide are substrates of these isoenzymes. If these drugs are to be used together, close monitoring of the patient for altered efficacy and safety is recommended.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76761/', '[1] Robertson P, Decory HH, Madan A, Parkinson A "In vitro inhibition and induction of human hepatic cytochrome P450 enzymes by modafinil." Drug Metab Dispos 28 (2000): 664-71[2] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[3] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[4] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.', 1767363022);
