INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76687, 'Cyclophosphamide', 'Pentostatin', 'Moderate', 'Coadministration of pentostatin with cyclophosphamide may enhance the cardiotoxic effects of cyclophosphamide. The mechanism of this interaction has not been delineated. Two cases of fatal cardiovascular collapse have been reported following administration of intravenous pentostatin with high-dose oral cyclophosphamide.', NULL, 'Patients receiving pentostatin and cyclophosphamide should be monitored for development of signs and symptoms of cardiotoxicity including, but not limited to respiratory distress, pulmonary edema, hypotension, hypothermia, and confusion.', NULL, 'Patients receiving pentostatin and cyclophosphamide should be monitored for development of signs and symptoms of cardiotoxicity including, but not limited to respiratory distress, pulmonary edema, hypotension, hypothermia, and confusion. Some authorities recommend avoiding the use of intravenous pentostatin with high-dose oral cyclophosphamide.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76788/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Cytoxan (cyclophosphamide)." Bristol-Myers Squibb, Princeton, NJ.[4] "Product Information. Nipent (pentostatin)." Hospira Inc, Lake Forest, IL.', 1767363022);
