INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76702, 'Cyclophosphamide', 'Roflumilast', 'Moderate', 'The concomitant use of roflumilast with immunosuppressive agents has not been adequately studied. Specifically, roflumilast and its active metabolite, roflumilast N-oxide, suppress the release of inflammatory mediators including leukotriene B4, reactive oxygen species, tumor necrosis factor alpha, interferon gamma, and granzyme B. Roflumilast also reduces sputum neutrophils and attenuates influx of neutrophils and eosinophils into the airways.', NULL, 'According to the product labeling for roflumilast in some countries , concomitant use with immunosuppressive agents such as methotrexate, azathioprine, infliximab, etanercept, and systemic corticosteroids (except when used short-term, e.', NULL, 'According to the product labeling for roflumilast in some countries , concomitant use with immunosuppressive agents such as methotrexate, azathioprine, infliximab, etanercept, and systemic corticosteroids (except when used short-term, e.g., in the treatment of COPD exacerbations) should be avoided if possible. Due to a lack of clinical experience, treatment with roflumilast should not be initiated, or existing treatment should be stopped, in patients receiving immunosuppressive agents and in patients with severe acute infectious diseases, cancers (except basal cell carcinoma), or severe immunological diseases (e.g., HIV infection, multiple sclerosis, lupus erythematosus, progressive multifocal leukoencephalopathy).', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76803/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Daxas (roflumilast)." Nycomed Inc, Princeton, NJ.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Daxas (roflumilast)." Nycomed Inc, Princeton, NJ.[5] "Product Information. Daliresp (roflumilast)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76703, 'Cyclophosphamide', 'Rotavirus vaccine', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76804/', '[1] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine.