INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76724, 'Cyclophosphamide', 'Tamoxifen', 'Moderate', 'Coadministration of tamoxifen with cyclophosphamide may increase the risk of thromboembolic complications. The mechanism of this interaction has not been delineated.', NULL, 'Patients receiving tamoxifen with cyclophosphamide should receive clinical and laboratory monitoring for the development of thromboembolic events.', NULL, 'Patients receiving tamoxifen with cyclophosphamide should receive clinical and laboratory monitoring for the development of thromboembolic events. It is not known whether prophylactic anticoagulation or antiplatelet therapy may lessen the risk of thromboembolic events; however, some authorities suggest that prophylactic antithrombotic measures should be considered if cyclophosphamide is given with tamoxifen. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain, shortness of breath, and pain or swelling in the arms or legs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76825/', '[1] Pritchard KI, Paterson AH, Paul NA, Zee B, Fine S, Pater J "Increased thromboembolic complications with concurrent tamoxifen and chemotherapy in a randomized trial of adjuvant therapy for women with breast cancer. National Cancer Institute of Canada Clinical Trials Group Breast Cancer Site Group." J Clin Oncol 14 (1996): 2731-7[2] "Product Information. Nolvadex (tamoxifen)." Zeneca Pharmaceuticals, Wilmington, DE.[3] "Product Information. Cytoxan (cyclophosphamide)." Bristol-Myers Squibb, Princeton, NJ.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Therapeutic Research Faculty "Natural Medicines Comprehensive Database. Available from: URL: http://www.naturaldatabase.com." ([1995-2008...]):', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76725, 'Cyclophosphamide', 'Temsirolimus', 'Moderate', 'Coadministration of sirolimus or tacrolimus with other drugs that are also metabolized by CYP450 3A4 may result in elevated plasma concentrations of the macrolide immunosuppressant and/or the coadministered drug(s). The mechanism is decreased drug clearance due to competitive inhibition of CYP450 3A4 activity.', NULL, 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', NULL, 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76826/', '[1] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[2] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[7] "Product Information. Tori