INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76791, 'Cyclosporine', 'Dabrafenib', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9. Dabrafenib has been found in vitro to be an inducer of these isoenzymes.', NULL, 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9.', NULL, 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dabrafenib is added to or withdrawn from therapy. Significantly reduced plasma concentrations and loss of efficacy may occur with sensitive substrates of CYP450 3A4 or 2C9 such as hormonal contraceptives, immunosuppressants (cyclosporine, everolimus, sirolimus, tacrolimus), ivacaftor, and warfarin-type anticoagulants. Alternatives to these medications should be considered if possible.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76892/', '[1] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[4] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84[5] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76792, 'Daclatasvir', 'Cyclosporine', 'Moderate', 'Coadministration with daclatasvir may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by daclatasvir.', NULL, 'Caution is advised when daclatasvir is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when daclatasvir is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever daclatasvir is added to or withdrawn from therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76893/', '[1] "Product Information. Daklinza (daclatasvir)." Bristol-Myers Squibb, Princeton, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76793, 'Dactinomycin', 'Cyclosporine', 'Moderate', 'Limited data suggest that cyclosporine given to reverse multidrug resistance can enhance the toxicity of some antineoplastic agents such as dactinomycin and vincristine. The proposed mechanism is cyclosporine inhibition of multidrug resistance protein 1 (MDR1), also known as P-glycoprotein, a plasma membrane efflux transporter that has been demonstrated as a primary cause of multidrug resistance in tumors. Inhibiting P-glycoprotein increases the intracellular concentration of substrate antineoplastic agents, thereby potentiating their cytotoxic effects.', NULL, 'Patients receiving antineoplastic agents that are known substrates of P-glycoprotein should be closely monitored for potentially increased toxicities during coadministration with cyclosporine, and their dosages adjusted as necessary.', NULL, 'Patients receiving antineoplastic agents that are known substrates of P-glycoprotein should be closely monitored for potentially increased toxicities during coadministration with cyclosporine, and their dosages adjusted as necessary.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76894/', '[1] Cowie F, Pinkerton CR "Enhanced toxicity of dactinomycin and vincristine by cyclosporine given to reverse multidrug resistance." J Clin Oncol 12 (1994): 1998-9[2] Ducharme MP, Warbasse LH, Edwards DJ "Disposition of intravenous and oral cyclosporine after administration with grapefruit juice." Clin Pharmacol Ther 57 (1995): 485-91[3] Min DI, Ku YM, Perry PJ, Ukah FO, Ashton K, Martin MF, Hunsicker LG "Effect of grapefruit juice on cyclosporine pha