INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76821, 'Dronedarone', 'Cyclosporine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of dronedarone, which is primarily metabolized by the isoenzyme. Because dronedarone is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'The use of dronedarone in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, cyclosporine, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics is considered contraindicated.', NULL, 'The use of dronedarone in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, cyclosporine, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics is considered contraindicated. Patients currently receiving these agents should stop taking them before starting treatment with dronedarone. Some authorities consider concomitant administration of dronedarone and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76922/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[4] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (76822, 'Drospirenone', 'Cyclosporine', 'Moderate', 'The use of drospirenone may potentiate the risk of hyperkalemia in patients receiving chronic treatment with drugs that can increase serum potassium such as angiotensin converting enzyme (ACE) inhibitors, angiotensin II receptor antagonists, potassium-sparing diuretics, aldosterone antagonists, nonsteroidal anti-inflammatory drugs (NSAIDs), potassium preparations, and miscellaneous agents like cyclosporine, heparin, tacrolimus, and trimethoprim. Drospirenone has antimineralocorticoid activity and may induce hyperkalemia in susceptible patients.', NULL, 'Serum potassium levels should be checked regularly during concomitant therapy with drospirenone and other agents that can increase serum potassium.', NULL, 'Serum potassium levels should be checked regularly during concomitant therapy with drospirenone and other agents that can increase serum potassium. Particular caution is warranted in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76923/', '[1] Ponce SP, Jennings AE, Madias NE, Harrington JT "Drug-induced hyperkalemia." Medicine (Baltimore) 64 (1985): 357-70[2] "Product Information. Yasmin (drospirenone-ethinyl estradiol)" Berlex Laboratories, Richmond, CA.', 1767363022);
