INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77094, 'Cyclosporine', 'Risankizumab', 'Moderate', 'The use of interleukin blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin inhibitors in patients with chronic inflammatory diseases.', NULL, 'Concomitant use of interleukin blockers with other immuno- or myelosuppressive agents should be avoided if possible.', NULL, 'Concomitant use of interleukin blockers with other immuno- or myelosuppressive agents should be avoided if possible. Caution is advised when interleukin inhibitors are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges such as immunosuppressants or antineoplastic agents. Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of interleukin inhibitor therapy, and the dosage(s) of these drugs adjusted accordingly. Clinicians should note that the effects of interleukin inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77195/', '[1] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc, Tarrytown, NY.[2] "Product Information. Sylvant (siltuximab)." Janssen Biotech, Inc., Horsham, PA, PA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Actemra (tocilizumab)." Genentech, South San Francisco, CA.[5] "Product Information. Stelara (ustekinumab)." Centocor Inc, Malvern, PA.[6] "Product Information. Amevive (alefacept)." Biogen, Cambridge, MA.[7] "Product Information. Taltz Autoinjector (ixekizumab)." Eli Lilly and Company, Indianapolis, IN.[8] "Product Information. Cosentyx (secukinumab)." Novartis Pharmaceuticals, East Hanover, NJ.[9] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77095, 'Cyclosporine', 'Ritonavir', 'Major', 'Coadministration with protease inhibitors (PIs), especially ritonavir, may significantly increase the blood concentrations of cyclosporine. The risk of nephro- and neurotoxicity associated with cyclosporine may be increased. The mechanism involves PI inhibition of CYP450 3A4, the isoenzyme responsible for the intestinal and hepatic metabolism of cyclosporine. Enhanced cyclosporine oral bioavailability due to PI inhibition of intestinal P-glycoprotein efflux transporter may also contribute.', NULL, 'Caution is advised if cyclosporine is used in combination with protease inhibitors.', NULL, 'Caution is advised if cyclosporine is used in combination with protease inhibitors. Cyclosporine blood levels and renal function should be checked frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of PI therapy in patients who are stabilized on their cyclosporine regimen. Patients should be advised to notify their physician if they experience possible signs of cyclosporine toxicity such as nausea, vomiting, diarrhea, abdominal pain, dizziness, fatigue, headache, tremors, and convulsions. Patients treated with saquinavir should also be monitored for potential PI toxicity and the dosage adjusted accordingly.', 'Metabolism, Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77196/', '[1] Brinkman K, Huysmans F, Burger DM "Pharmacokinetic interaction between saquinavir and cyclosporine." Ann Intern Med 129 (1998): 914-5[2] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[3] "Product Infor