INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77199, 'Cyclosporine', 'Ubrogepant', 'Moderate', 'Coadministration with breast cancer resistance protein (BCRP) and/or P-glycoprotein (P-gp) only inhibitors may increase the exposure of ubrogepant, which is a substrate of these transporters.', NULL, 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with BCRP and/or P-gp only inhibitors.', NULL, 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with BCRP and/or P-gp only inhibitors. If needed, a second 50 mg dose of ubrogepant may be administered at least 2 hours after the initial dose.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77300/', '[1] "Product Information. Ubrelvy (ubrogepant)." Allergan Inc, Irvine, CA.[2] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Ubrelvy (ubrogepant)." Allergan Inc, Irvine, CA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77200, 'Cyclosporine', 'Ulipristal', 'Moderate', 'Based on in vitro inhibition data, coadministration with ulipristal may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by ulipristal.', NULL, 'Caution is advised when ulipristal is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when ulipristal is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ulipristal is added to or withdrawn from therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77301/', '[1] "Product Information. ella (ulipristal)." Afaxys Inc., Charleston, SC.', 1767363022);
