INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77481, 'Dabrafenib', 'Acetohexamide', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9, including many antidiabetic agents such as nateglinide, repaglinide, linagliptin, saxagliptin, and sulfonylureas.', NULL, 'Caution is advised when dabrafenib is prescribed to patients with diabetes, particularly those receiving antidiabetic agents that are metabolized by CYP450 3A4 and/or 2C9.', NULL, 'Caution is advised when dabrafenib is prescribed to patients with diabetes, particularly those receiving antidiabetic agents that are metabolized by CYP450 3A4 and/or 2C9. Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of dabrafenib, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination. Likewise, patients should be observed for hypoglycemia when dabrafenib is withdrawn from their therapeutic regimen.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77582/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.[4] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[5] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[6] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[7] "Position Statement: evidence-based nutrition principles and recommendations for the treatment and prevention of diabetes related complications. American Diabetes Association." Diabetes Care 25(Suppl 1) (2002): S50-S60[8] "Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[9] Jerntorp P, Almer LO, Holin H, et al "Plasma chlorpropamide: a critical factor in chlorpropamide-alcohol flush." Eur J Clin Pharmacol 24 (1983): 237-42[10] Hartling SG, Faber OK, Wegmann ML, Wahlin-Boll E, Melander A "Interaction of ethanol and glipizide in humans." Diabetes Care 10 (1987): 683-6[11] Barnett AH, Spiliopoulos AJ, Pyke DA, et al "Metabolic studies in chlorpropamide-alcohol flush positive and negative type 2 (non-insulin dependent) diabetic patients with and without retinopathy." Diabetologia 24 (1983): 213-5[12] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[13] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[14] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77482, 'Aluminum hydroxide', 'Dabrafenib', 'Moderate', 'Drugs that alter the pH of the upper gastrointestinal tract may affect the solubility of dabrafenib and reduce its bioavailability. The interaction has not been formally studied with H2-receptor antagonists or antacids; however, the solubility of dabrafenib is known to be pH-dependent. It has been reported to be very slightly soluble at pH 1 and practically insoluble above pH 4 in aqueous media.', NULL, 'Until more information is available, caution is advised if dabrafenib is used in combination with H2-receptor antagonists or antacids.', NULL, 'Until more information is available, caution is advised if dabrafenib is used in combination with H2-receptor antagonists or antacids. The potential for diminished therapeutic effects of dabrafenib should be considered, and pharmacologic response should be closely monitored.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77583/', '[1] "Product Information. Prevacid (lansoprazole)." TAP Pharmaceuticals Inc, Deerfield, IL.[2] "Product Information. Zantac (ranitidine)." Glaxo Wellcome, Research Triangle Park, NC.[3] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.[4] "Product Information. Dexilant (dexlansoprazole)." Takeda Pharmaceuticals America, Lincolnshire, IL.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] "Product Information. Aciphex (rabeprazole)" Janssen Pharmaceuticals, Titusville, NJ.[8] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77483, 'Aminolevulinic acid', 'Dabrafenib', 'Major', 'Patients exposed to photosensitizing agents may be at increased risk of a phototoxic skin reaction (severe sunburn) during use of aminolevulinic acid.', NULL, 'Photosensitizing agents (e.', NULL, 'Photosensitizing agents (e.g., fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, St. John''s wort) and topical preparations containing aminolevulinic acid should generally not be administered for 24 hours before and after oral administration of aminolevulinic acid.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77584/', '[1] "Product Information. Gleolan (aminolevulinic acid)." NX Development Corp, Lexington, KY.[2] "Product Information. Nulibry (fosdenopterin)." Origin Biosciences Inc, Boston, MA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77484, 'Amprenavir', 'Dabrafenib', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 and/or 2C8 may increase the plasma concentrations of dabrafenib and its active metabolites. Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4, including many of the known inhibitors of the isoenzyme such as conivaptan, delavirdine, nefazodone, telithromycin, and most azole antifungal agents, macrolide antibiotics, and protease inhibitors.', NULL, 'The use of dabrafenib with potent CYP450 2C8 inhibitors such as gemfibrozil or potent CYP450 3A4 inhibitors such as ceritinib, clarithromycin, cobicistat, conivaptan, delavirdine, erythromycin, idelalisib, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents should generally be avoided if possible.', NULL, 'The use of dabrafenib with potent CYP450 2C8 inhibitors such as gemfibrozil or potent CYP450 3A4 inhibitors such as ceritinib, clarithromycin, cobicistat, conivaptan, delavirdine, erythromycin, idelalisib, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents should generally be avoided if possible. Some authorities recommend avoiding concomitant use of dabrafenib during and for 2 weeks after treatment with itraconazole. Otherwise, patients should be closely monitored for development of adverse effects such as febrile reactions (high fever or fever accompanied by rigors, hypotension, dehydration, or renal failure), hyperglycemia, uveitis, and cutaneous malignancies (e.g., squamous cell carcinoma, keratoacanthoma, melanoma).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77585/', '[1] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[6] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.', 1767363022);
