INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369896, 'Glyburide', 'Bosentan', 'Major', 'In the REACH-1 trial, an increased incidence of elevated liver transaminases was observed in the subgroup of patients receiving bosentan in combination with glyburide relative to the bosentan group as a whole or those receiving placebo . The exact mechanism of interaction is unknown but may be partially due to additive inhibition of the hepatocellular bile salt export pump, resulting in increased hepatocyte concentrations of toxic bile salts. In REACH-1, bosentan-induced elevations in liver transaminase levels were asymptomatic and reversible and did not result in any signs of clinically significant liver disease. Pharmacokinetically, coadministration of the two drugs can result in decreased plasma concentrations of both.', 'DDInter', 1767317475, 'According to the manufacturer, the concomitant use of glyburide and bosentan is contraindicated. Alternative hypoglycemic agents should be considered.', 'Synergism', 'According to the manufacturer, the concomitant use of glyburide and bosentan is contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
