INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369915, 'Istradefylline', 'Bosentan', 'Moderate', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of istradefylline, which is primarily metabolized by CYP450 3A4 and 1A1. No data are available regarding the effects that other, less potent CYP450 3A4 inducers will have on the pharmacokinetics of istradefylline.', 'DDInter', 1767317475, 'Caution is advised if istradefylline is used with moderate CYP450 3A4 inducers. Clinical monitoring of istradefylline should be considered whenever a moderate CYP450 3A4 inducer is added to or withdrawn from therapy. Patients should be monitored for diminished therapeutic effects.', 'Metabolism', 'Caution is advised if istradefylline is used with moderate CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369916, 'Itraconazole', 'Bosentan', 'Moderate', 'Coadministration of bosentan with a drug that is both a substrate as well as inhibitor of CYP450 2C9 and/or 3A4 may result in increased plasma concentrations of bosentan and decreased plasma concentrations of the other drug. Bosentan itself is a substrate and inducer of both CYP450 2C9 and 3A4. Theoretically, bosentan may induce metabolism of the coadministered drug while its own metabolism may be inhibited by the coadministered drug.', 'DDInter', 1767317475, 'When a drug that is both a substrate as well as inhibitor of CYP450 2C9 and/or 3A4 is coadministered with bosentan, the possibility of diminished therapeutic response to the coadministered drug should be considered. Clinical and/or laboratory monitoring may be appropriate whenever bosentan is added to or withdrawn from therapy, and the dosage of the concomitant drug adjusted as necessary. The possibility of prolonged and/or increased pharmacologic effects of bosentan, including serious adverse effects such as hepatotoxicity, should also be considered. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. Concomitant administration of bosentan with both a potent CYP450 2C9 inhibitor (e.g., fluconazole, amiodarone) and a potent CYP450 3A4 inhibitor (e.g., ketoconazole, itraconazole, ritonavir) is not recommended. Concomitant administration with combination CYP450 2C9/3A4 inhibitors (e.g., delavirdine, imatinib, miconazole, mifepristone, voriconazole) should probably be avoided also, if possible.', 'Metabolism', 'When a drug that is both a substrate as well as inhibitor of CYP450 2C9 and/or 3A4 is coadministered with bosentan, the possibility of diminished therapeutic response to the coadministered drug should be considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369917, 'Ivabradine', 'Bosentan', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ivabradine and its active N-desmethylated metabolite, both of which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised during concomitant use of ivabradine with CYP450 3A4 inducers. The potential for decreased efficacy of ivabradine should be considered.', 'Metabolism', 'Caution is advised during concomitant use of ivabradine with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369918, 'Ivacaftor', 'Bosentan', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of ivacaftor-containing medications should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of ivacaftor-containing medications should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
